import{_ as r,a as c,b as i}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.BCVHfIj7.js";import{_ as n,c as d,I as t,a7 as s,o as l}from"./chunks/framework.DGCt2JJv.js";import"./chunks/dayjs.min.DZyl58SH.js";const k=JSON.parse('{"title":"圈复杂度Cyclomatic complexity","description":"","frontmatter":{},"headers":[],"relativePath":"standard/圈复杂度.md","filePath":"standard/圈复杂度.md","lastUpdated":1709970389000}'),_={name:"standard/圈复杂度.md"},m=s('<h1 id="圈复杂度cyclomatic-complexity" tabindex="-1">圈复杂度Cyclomatic complexity <a class="header-anchor" href="#圈复杂度cyclomatic-complexity" aria-label="Permalink to &quot;圈复杂度Cyclomatic complexity&quot;">​</a></h1><h2 id="什么是圈复杂度" tabindex="-1">什么是圈复杂度 <a class="header-anchor" href="#什么是圈复杂度" aria-label="Permalink to &quot;什么是圈复杂度&quot;">​</a></h2><p>圈复杂度（Cyclomatic complexity，简写 CC）也称为条件复杂度，是模块结构复杂度的度量，数量上表现为独立路径的条数，即合理的预防错误所需测试的最少路径条数。 成立于1976年的McCabe&amp;Associates公司开发出了McCabe Cyclomatic Complexity Metric（圈复杂度）技术。 Metric以软件复杂度测量的数目为基础，能帮助工程师识别难于测试和维护的模块，圈复杂度已经成为评估软件质量的一个重要标准。 人们可以用圈复杂度对软件的复杂度和质量进行衡量，来安排工程进度，在成本、进度和性能之间寻求平衡。</p><p>研究表明，程序的Cyclomatic复杂性与其可维护性和可测试性之间存在相关性，这意味着对于更高复杂性的文件，在修复、增强或重构源代码时出错的概率更高。 但是要注意，McCabe度量数大的程序，不见得结构化就不好。 例如，Case语句是良结构的，但可能有很大的McCabe度量数（依赖于语句中的分支数），这可能是由于问题和解决方案所固有的复杂性所决定的。 使用者应当自己决定如何使用McCabe度量所提供的信息。</p><h2 id="圈复杂度阈值" tabindex="-1">圈复杂度阈值 <a class="header-anchor" href="#圈复杂度阈值" aria-label="Permalink to &quot;圈复杂度阈值&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Cyclomatic Complexity</th><th>Risk Evaluation</th></tr></thead><tbody><tr><td>1-10</td><td>一个没有太大风险的简单模块</td></tr><tr><td>11-20</td><td>具有中等风险的更复杂模块</td></tr><tr><td>21-50</td><td>高风险的复杂模块</td></tr><tr><td>51 and greater</td><td>风险极高的不稳定项目</td></tr></tbody></table><h2 id="我为什么建议前端重视「圈复杂度」" tabindex="-1">我为什么建议前端重视「圈复杂度」？ <a class="header-anchor" href="#我为什么建议前端重视「圈复杂度」" aria-label="Permalink to &quot;我为什么建议前端重视「圈复杂度」？&quot;">​</a></h2><p><a href="https://juejin.cn/post/7340899332509368357" target="_blank" rel="noreferrer">https://juejin.cn/post/7340899332509368357</a></p><h2 id="contributors" tabindex="-1">Contributors <a class="header-anchor" href="#contributors" aria-label="Permalink to &quot;Contributors&quot;">​</a></h2>',9);function h(p,b,u,C,f,y){const a=r,e=c,o=i;return l(),d("div",null,[m,t(a,{readTime:"1",words:"441"}),t(e),t(o)])}const M=n(_,[["render",h]]);export{k as __pageData,M as default};
